mapper.select:
SELECT post_id, post_slug, post_title, post_excerpt, post_create_time, post_comment_count, post_read_count, post_spot_count FROM post_table
WHERE post_type='post' AND post_status='publish' AND post_id IN

    #{item}

ORDER BY post_create_time DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE post_table
SET comment_count+=#{count}
WHERE post_id=#{id}

UPDATE post_table
SET read_count+=#{count}
WHERE post_id=#{id}

UPDATE post_table
SET spot_count+=#{count}
WHERE post_id=#{id}
